import React, { useState } from 'react';

function Communication() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const handleSend = async () => {
    try {
      // Call the backend to filter the message
      const response = await fetch('/api/filter', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message })
      });
      const data = await response.json();
      // Append the filtered message to the chat history
      setChatHistory([...chatHistory, { original: message, filtered: data.filteredMessage }]);
      setMessage('');
    } catch (err) {
      console.error('Error filtering message:', err);
    }
  };

  return (
    <div>
      <h3>Communication</h3>
      <div
        style={{ border: '1px solid #ccc', padding: '1rem', height: '300px', overflowY: 'scroll' }}
      >
        {chatHistory.map((msg, index) => (
          <div key={index}>
            <p><strong>You:</strong> {msg.filtered}</p>
          </div>
        ))}
      </div>
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        rows={4}
        style={{ width: '100%', marginTop: '1rem' }}
      />
      <button onClick={handleSend} style={{ marginTop: '0.5rem' }}>
        Send
      </button>
    </div>
  );
}

export default Communication;
