import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import LinkPartner from './components/LinkPartner';
import MainApp from './components/MainApp';
import './styles.css';

function App() {
  const [user, setUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const testAccount = {
    username: 'dev',
    password: 'deviscool123'
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === testAccount.username && password === testAccount.password) {
      setError('');
      alert('Login successful!');
      // Here you would typically redirect or set authentication state
    } else {
      setError('Invalid credentials. Use username: dev, password: deviscool123');
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/link-partner" element={user ? <LinkPartner user={user} /> : <Navigate to="/login" />} />
        <Route path="/app/*" element={user ? <MainApp /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
      <div className="login-container">
        <h2>Coparently Login</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Log In</button>
          {error && <div className="error">{error}</div>}
        </form>
      </div>
    </Router>
  );
}

export default App;
